import './mac-window.css';
import './language';
// import SwitchImputs from './menuswitch';
import EmailInput from './input/email-input';
import ArticleInput from './input/article-input';
import EssayInput from './input/essay-input';

function MacWindow() {

    const menuLinks = document.querySelectorAll('.menu-link');
    // create a constant that selects all the menu links
    
    if (menuLinks.length) {
      menuLinks.forEach((menuLink) => {
        menuLink.addEventListener('click', (e) => {
          menuLinks.forEach((menuLink) => {
            menuLink.classList.remove('is-active');
            // remove the is-active class from all the menu links
          });
          e.preventDefault();
          menuLink.classList.add('is-active');
          // add the is-active class to the clicked menu link
        });
      });
    }

  return (
    <div className="body">
        <div className="app">
            {/* Header */}
            <div className="header" id='header'>
                <div className="dots">
                    <div className="menu-circle"></div>
                </div>
                <div className="header-menu">
                    <a className="menu-link is-active" href="#" id='email' >Email</a>
                    <a className="menu-link" href="#" id='article' >Article</a>
                    <a className="menu-link" href="#" id='essay' >Essay</a>
                    <a className="menu-link" href="#" id='anything' >Ask Anything</a>
                </div>
                <div className="header-profile">
                    <img className="profile-img" src="data:image/jpeg;base64," alt=""></img>
                </div>
            </div>
            {/* Main */}
            {/* window 1 */}
            <div className="input-section">
                <EmailInput />
                <ArticleInput />
                <EssayInput />
            </div>
            {/* window 2 */}
            <div className="output-section">
                <div className="output"></div>
                <div className="interaction">
                    <textarea className="input-text" autoComplete='off' placeholder="Ask anything" />
                    {/* <button></button> */}
                </div>
            </div>
        </div>
    </div>
  );
}

export default MacWindow;
